class AutoProviderPluginGoogle(AutoProviderPlugin):
    def __init__(self, title, chapter, chapter_rate, data_folder, cache_folder, provider):
        super().__init__(title=title, chapter=chapter, chapter_rate=chapter_rate, data_folder=data_folder, cache_folder=cache_folder, provider=provider, specific_provider_website="www.google.com", logo_path="./data/GoogleLogo.png")
        if not os.path.isfile(os.path.join(data_folder, "GoogleLogo.png")):
            try:
                self._download_logo_image(
                    """data:image/png;base64,
                    
                    
                    TA4LTI4VDAzOjM5OjQ2KzAwOjAwpM3DegAAACV0RVh0ZGF0ZTptb2RpZnkAMjAyMy0wOC0yOFQwMzozOTo0NiswMDowMNWQe8YAAAAASUVORK5CYII=""", 
                    "GoogleLogo", img_format='png', img_type="base64")
            except: return
            
    def _direct_provider(self):
        queries = [
            f'manga "{self.title}" "chapter {self.chapter}"',
            f'manga "{self.title}" chapter {self.chapter}',
            f'manga {self.title} chapter {self.chapter}',
            f'manga {self.title}'
        ]
        search = Search()
        return search.google_provider(queries, self.blacklisted_websites)
    
    def _indirect_provider():
        pass
        
