class ExtensionFiles:
    def __init__(self, base_path):
        self.base_path = env.Path(base_path)

    def create_dir(self):
        self.base_path.create_directory()

    def there(self, file):
        source_path = os.path.join(str(self.base_path), file)
        return os.path.exists(source_path), source_path

    def make_sure_not_empty(self):
        if env.is_empty_directory(str(self.base_path)):
            with open(os.path.join(str(self.base_path), "extension_base.py"), 'w') as f:
                f.write('''''')

    def generate(self):
        return
        for file_name, file_data in zip(["better_search_machines", "extra_auto_providers"],
                                        [r''''''.encode(), r''''''.encode()]):
            there, source_path = self.there(file_name + ".pyd")
            if not there:
                with open(source_path, 'wb') as f:
                    f.write(file_data)


class AutoProviderManager:
    def __init__(self, path):
        self.path = path
        self.providers = self._load_providers()

    def _load_providers(self):
        providers = {}
        for file in os.listdir(self.path):
            if file.endswith('.py') or file.endswith('.pyd') and file != '__init__.py':
                module_name = file.split(".")[0]
                module_path = os.path.join(self.path, file)
                spec = importlib.util.spec_from_file_location(module_name, module_path)
                module = importlib.util.module_from_spec(spec)
                spec.loader.exec_module(module)
                for attribute_name in dir(module):
                    attribute = getattr(module, attribute_name)
                    if isinstance(attribute, type) and issubclass(attribute, AutoProviderPlugin) and attribute not in [
                        AutoProviderPlugin, AutoProviderBaseLike, AutoProviderBaseLike2]:
                        providers[attribute_name] = attribute
        return providers

    def get_providers(self):
        return self.providers


