    def boolean(self, to_boolean: str) -> bool:
        return to_boolean.lower() == "true"

    def str_to_list(self, s: str) -> List[str]:
        return s.split(", ")

    def list_to_str(self, lst: List[str]) -> str:
        return ', '.join(lst)

    def get(self, key: str):
        value = self.settings.get(key)
        if key in ["blacklisted_websites", "export_settings"]:
            return self.str_to_list(value)
        elif key in ["chapter"]:
            return int(value) if float(value).is_integer() else float(value)
        elif key in ["chapter_rate"]:
            return float(value)
        elif key in ["downscaling", "upscaling", "borderless", "invisible_background", "hide_scrollbar", "stay_on_top",
                     "auto_export",
                     "no_update_info", "update_info"]:
            return self.boolean(value)
        elif key in ["manual_content_width"]:
            return int(value)
        elif key in ["geometry", "last_scroll_positions"]:
            return [int(x) for x in value.split(", ")]
        return value

    def set(self, key: str, value):
        if key in ["blacklisted_websites", "export_settings"]:
            value = self.list_to_str(value)
        elif key in ["chapter"]:
            value = str(int(value) if float(value).is_integer() else value)
        elif key in ["chapter_rate"]:
            value = str(float(value))
        elif key in ["downscaling", "upscaling", "borderless", "invisible_background", "hide_scrollbar", "stay_on_top",
                     "auto_export",
                     "no_update_info", "update_info"]:
            value = str(value)
        elif key in ["manual_content_width"]:
            value = str(int(value))
        elif key in ["geometry", "last_scroll_positions"]:
            value = ', '.join([str(x) for x in value])
        self.settings[key] = value
        self.update_data()

