    def toggle_search(self):
        is_visible = self.search_widget.y() >= 0
        if is_visible:
            end_rect = QRect(0, -self.search_widget.height(), self.search_widget.width(), self.search_widget.height())
        else:
            end_rect = QRect(0, self.search_toggle_button.height() - 20, self.search_widget.width(),
                             self.search_widget.height())
        self.search_animation.setStartValue(self.search_widget.geometry())
        self.search_animation.setEndValue(end_rect)
        self.search_animation.start()

