class ZinMangaProvider(ManhwaLikeProvider):
    register_provider_name: str = "ZinManga [js-mode]"
    register_provider_id: str = "zin_manga"

    def __init__(self, title: str, chapter: int, library_path: str, logo_folder: str) -> None:
        logo = ProviderImage("logo_zinmanga", "png", "base64", "data:image/png;base64,")
        super().__init__(title=title, chapter=chapter, library_path=library_path, logo_folder=logo_folder,
                         specific_provider_website="zinmanga.net", logo=logo, icon=None, enable_js=True)
        self.clipping_space = (0, 0, -1, -1)

    def _get_current_chapter_url(self) -> str | None:
        url = f"https://{self._specific_provider_website}/manga/{slugify(self._title)}/" + f"chapter-{self._chapter_str}/"
        print("Found URL:" + url)  # Concatenate (add-->+) string, to avoid breaking timestamps
        return url

    def _search_post(self, search_text: str) -> dict | None:
        url = f"https://www.zinmanga.net/manga/searching?title={search_text}"
        headers = {
            'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36',
            'Accept': 'application/json',
        }

        try:
            response = requests.get(url, headers=headers, timeout=5)
            response.raise_for_status()
            data = response.json()

            if not data.get("success") or not data.get("data"):
                print(f"[ZINMANGA] No results for: {search_text}")
                return {"data": []}

            results = []
            for item in data["data"]:
                results.append({
                    "title": item.get("name"),
                    "url": item.get("original_url"),
                    "slug": item.get("slug"),
                    "status": item.get("status"),
                    "author": item.get("author"),
                    "genres": item.get("kind"),
                    "cover": f"https://www.zinmanga.net{item.get('image_original')}" if item.get("image_original") else None,
                    "latest_chapter": json.loads(item.get("chapter_latest", "[]"))[0]["chapter_name"] if item.get("chapter_latest") else None,
                    "description": item.get("detail"),
                    "rating": item.get("rating_value"),
                })

            return {"data": results}

        except requests.RequestException as e:
            print(f"[ZINMANGA] Request failed: {e}")
            return {"data": []}
        except Exception as e:
            print(f"[ZINMANGA] Unexpected error: {e}")
            return {"data": []}

    def _search_web(self, search_text: str) -> dict:
        base_url = self._specific_provider_website
        search_url = f"https://{base_url}/?s={search_text}&post_type=wp-manga"

        print("SEARURL", search_url)
        try:
            response = requests.get(search_url)
            response.raise_for_status()
        except requests.RequestException:
            return {"success": False, "data": [""]}

        soup = BeautifulSoup(response.content, 'html.parser')
        c_tabs_item_divs = soup.find_all('div', class_='c-tabs-item')  # There is only ever one of these

        # Dictionary to hold titles and urls
        titles_urls = []

        # Iterate through each div to extract titles and urls
        for div in c_tabs_item_divs:
            for row_c_tabs in div.find_all('div', class_='row c-tabs-item__content'):
                a_tag = row_c_tabs.find('h3').find("a")
                if a_tag and 'href' in a_tag.attrs:
                    titles_urls.append({"title": a_tag.text, "url": a_tag['href']})

        return {"data": titles_urls}

    def _search(self, search_text):
        text = search_text
        search_results = self._search_web(text)
        if not search_results["data"]:
            return self._search_post(text)
        return search_results

    def is_working(self) -> bool:
        return True


class MangaOI(ManhwaLikeProvider):
    register_provider_name = "MangaOI [js-mode]"
    register_provider_id = "manga_oi"

    def __init__(self, title: str, chapter: int, library_path: str, logo_folder: str) -> None:
        logo = ProviderImage("logo_mangaoi", "png", "base64", "data:image/png;base64,")
        super().__init__(title=title, chapter=chapter, library_path=library_path, logo_folder=logo_folder,
                         specific_provider_website="mangaoi.net", logo=logo, icon=None, enable_js=True)
        self.clipping_space = (0, 0, -1, -1)

    def _get_current_chapter_url(self) -> str | None:
        url = f"https://{self._specific_provider_website}/read-manga/{slugify(self._title)}/" + f"chapter-{self._chapter_str}/"
        print("Found URL:" + url)  # Concatenate (add-->+) string, to avoid breaking timestamps
        return url

    def get_search_results(self, search_text: str | None) -> bool | list[tuple[str, str]]:
        if search_text is None:  # Can't search, because their impl is bad af
            return False

    def _be_picky(self, soup: BeautifulSoup) -> list[dict]:
        # Find the div with class 'chapter-content'
        reading_content_div = soup.find('div', class_='chapter-content')

        if not reading_content_div:
            return []

        # Extract all image elements within the div
        images = reading_content_div.find_all('img')

        # Define a regex pattern to filter out base64 images (data:...)
        valid_images = []
        for img in images:
            src = img.get('src', '')
            if src and not re.match(r'^data:.*', src):  # Ensures src is not base64
                valid_images.append(img)

        return valid_images


class AsuraToonProvider(OnlineProvider):
    register_provider_name: str = "AsuraToon"
    register_provider_id: str = "asura_toon"

    def __init__(self, title: str, chapter: int, library_path: str, logo_folder: str) -> None:
        super().__init__(title, chapter, library_path, logo_folder)
        self._logo_path: str = os.path.join(logo_folder, "logo_asuratoon.png")
        if os.path.isfile(self._logo_path):
            return
        try:
            # Using base64 is better as it won't matter if the url is ever changed, otherwise pass the url and
            # img_type="url"
            self._download_logo_image(
                'data:image/png;base64,',
                "logo_asuratoon", img_format='png', img_type="base64")
        except Exception as e:
            print(f"An error occurred {e}")
            return
        self.clipping_space = (0, 0, -2, -2)

    def get_logo_path(self) -> str:
        return self._logo_path

    def get_icon_path(self) -> str:
        return ""

    def _search(self, query=None):
        base_url = "asuratoon.net"
        search_url = f"https://{base_url}?s={query or self._title}&post_type=wp-manga"

        response = requests.get(search_url)
        response.raise_for_status()

        soup = BeautifulSoup(response.content, 'html.parser')
        listupd_divs = soup.find_all('div', class_='c-tabs-item')  # There is only ever one of these

        # Dictionary to hold titles and urls
        titles_urls = {}

        # Iterate through each div to extract titles and urls
        for div in listupd_divs:
            for bsx in div.find_all('h3', class_='h4'):
                a_tag = bsx.find('a')
                if a_tag and 'href' in a_tag.attrs:
                    titles_urls[a_tag.get_text(strip=True)] = a_tag['href']

        return titles_urls

    def _get_current_chapter_url(self) -> str | None:
        response_data = self._search()
        base_url = response_data.get(next(iter(response_data)))
        chapter_url = urljoin(base_url, f"chapter-{self._chapter}")
        if chapter_url:
            print("Found URL:" + chapter_url)  # Concatenate (add-->+) string, to avoid breaking timestamps
            return chapter_url
        return None

    def get_search_results(self, text):
        if text is None:
            return True
        response_data = self._search(text)
        return tuple([title, ""] for title in response_data.keys())

    def _be_picky(self, soup: BeautifulSoup) -> list[dict]:
        # Find the div with class 'reading-content'
        reading_content_div = soup.find('div', class_='reading-content')

        if not reading_content_div:
            return []

        # Extract all image elements within the div
        images = reading_content_div.find_all('img')

        # Define a regex pattern to filter out base64 images (data:...)
        valid_images = []
        for img in images:
            src = img.get('src', '')
            if src and not re.match(r'^data:.*', src):  # Ensures src is not base64
                valid_images.append(img)

        return valid_images

    def can_work(self) -> bool:
        return False


class BaozimhProvider(ManhwaLikeProvider):
    register_provider_name: str = "Baozimh"
    register_provider_id: str = "baozimh"

    def __init__(self, title: str, chapter: int, library_path: str, logo_folder: str) -> None:
        logo = ProviderImage("logo_baozimh", "png", "base64", "data:image/png;base64,")
        super().__init__(title=title, chapter=chapter, library_path=library_path, logo_folder=logo_folder,
                         specific_provider_website="baozimh.org", logo=logo, icon=None, enable_js=True)
        self.clipping_space = (0, 0, -1, 144)

    def _search(self, query=None):
        search_url = f"https://{self._specific_provider_website}/s/{query or self._title}"

        response = requests.get(search_url)
        response.raise_for_status()

        soup = BeautifulSoup(response.content, 'html.parser')

        # Find the main container div
        main_div = soup.find('div', class_='md:grid-cols-6 grid-cols-3 cardlist')
        if not main_div:
            return {"data": []}  # Return empty list if the main div isn't found

        # Extract all relevant divs
        pb2_divs = main_div.find_all('div', class_='pb-2')

        titles_urls = []

        for div in pb2_divs:
            a_tag = div.find('a')  # Find the direct <a> inside the div
            if a_tag and 'href' in a_tag.attrs:
                title_tag = a_tag.find('h3')  # Look for <h3> inside <a>
                title = title_tag.text.strip() if title_tag else "Unknown Title"
                titles_urls.append({"title": title, "url": a_tag['href']})

        return {"data": titles_urls}

    def _get_chapter_url(self, page_url, chapter_number):
        response = requests.get(page_url)

        if response.status_code != 200:
            return "Failed to retrieve the web page."

        soup = BeautifulSoup(response.content, 'html.parser')

        # Find the button with id 'firstchap'
        first_chap_button = soup.find('button', id='firstchap')
        if not first_chap_button or 'data-cs' not in first_chap_button.attrs:
            return "First chapter button not found or missing data-cs attribute."

        content_number = first_chap_button['data-cs'].split("_")[0]

        base_name = urlparse(page_url).path.strip('/').split('/')[-1]

        return f"https://{self._specific_provider_website}/manga/{base_name}/{content_number}_{str(chapter_number).replace('.', '_')}" or page_url

    def _get_current_chapter_url(self):
        response_data = self._search()
        base_url = response_data["data"][0]["url"]
        chapter_url = self._get_chapter_url(base_url, self._chapter)

        if chapter_url:
            print("Found URL:" + chapter_url)  # Concatenate (add-->+) string, to avoid breaking timestamps
            return chapter_url
        return None

    def can_work(self) -> bool:
        return False


